import React, { useState } from "react";
import Game from "../Game";

const Home = () => {
  //! useState : tạo state
  // phần tử 1 : là 1 cái state
  // phần tử 2 : là 1 cái hàm
  //! useState : param 1 : là giá trị ban đầu của state
  const [isGameStarted, setIsGameStarted] = useState(false)



  return (
    <>
      {isGameStarted ? <Game /> : (
        <div
          className="text-center"
          style={{
            width: "100vw",
            height: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1 className="diplay-4 mb-5"> Welcome to Pocker Center</h1>
          <h3>Fill your info and start</h3>
          <form className="w-25 mx-auto">
            <input
              type="input"
              placeholder="username"
              className="w-100 form-control mb-3"
            />
            <input
              type="input"
              placeholder="email"
              className="w-100 form-control mb-3"
            />
            <input
              type="input"
              placeholder="phone"
              className="w-100 form-control mb-3"
            />
            <button className="btn btn-success">Start new Game</button>
          </form>
        </div>
      )}

      <Game />
    </>
  );
};

export default Home;
