import React, { useState, Fragment } from "react";
import Game from "../Game";
//! check Validate
import { useFormik } from 'formik';
import * as yup from 'yup';

//shape là hình dáng cái form như nào
//yup.object : value của cái form bắt buộc phải là object
const schema = yup.object().shape({
  //kiểu dữ liệu
  //username phải là chuỗi
  username: yup.string().required("** Username is Required ! "),
  email: yup.string().required("** Email is Required ! ").email("** Email is invalid, please try again"),
  // nhập 1 hoặc nhiều số ($) bắt buộc,
  phone: yup
    .string()
    .required("** Phone is Required !")
    .matches(/^[0-9]+$/g, "Phone must be number !")
})



const Home = () => {
  //! useState : tạo state
  // phần tử 1 : là 1 cái state
  // phần tử 2 : là 1 cái hàm
  //! useState : param 1 : là giá trị ban đầu của state
  const [isGameStarted, setIsGameStarted] = useState(false)

  // const [a, setA] = useState("");
  // const [b, setB] = useState(0);
  //! kiểu cũ
  // const [formValue, setFormValue] = useState({
  //   username: "",
  //   email: "",
  //   phone: "",
  // })
  //! xử lý form
  //todo values là data của form
  // const { handleChange, values } = useFormik({
  //   initialValues: {
  //     username: "",
  //     email: "",
  //     phone: "",
  //   }
  // })
  const formik = useFormik({
    initialValues: {
      username: "",
      email: "",
      phone: "",
    },
    validationSchema: schema,
    validateOnMount: true,
  })


  const handleSubmit = (event) => {
    event.preventDefault();
    // setIsGameStarted(true);
    //! có sẵn
    console.log(formik.values);
    console.log(formik.errors);
  };

  return (
    <Fragment>
      {isGameStarted ? <Game /> : (
        <div
          className="text-center"
          style={{
            width: "100vw",
            height: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1 className="diplay-4 mb-5"> Welcome to Pocker Center</h1>
          <h3>Fill your info and start</h3>
          <form
            onSubmit={handleSubmit}
            className="w-25 mx-auto">
            <input
              //state muốn cập nhật
              name="username"
              onChange={formik.handleChange}
              type="input"
              placeholder="username"
              className="w-100 form-control mb-3"
            />
            <p>{formik.errors.username}</p>
            <input
              name="email"
              onChange={formik.handleChange}
              type="input"
              placeholder="email"
              className="w-100 form-control mb-3"
            />
            <p>{formik.errors.phone}</p>
            <input
              name="phone"
              onChange={formik.handleChange}
              type="input"
              placeholder="phone"
              className="w-100 form-control mb-3"
            />
            <button className="btn btn-success">Start new Game</button>
          </form>
        </div>
      )}
    </Fragment>
  );
};

export default Home;
